/**
* Engine contructor
* @class
* @constructor
*/
export class SCE {
    constructor() {
        this.target = null;
        this.project = null;
        this.dimensions = null;
        this.canvas = null;
        this.gl = null;
        this._UI = null;
        this._enableUI = null;
        this._events = null;

        this.sceDirectory = document.querySelector('script[src$="SCE.class.js"]');
        if(this.sceDirectory === null)
            this.sceDirectory = document.querySelector('script[src$="SCEJS.min.js"]');
        this.sceDirectory = this.sceDirectory.getAttribute('src');

        let page = this.sceDirectory.split('/').pop();
        this.sceDirectory = this.sceDirectory.replace('/'+page,"");
    }

	/**
	* @param {Object} jsonIn
	* @param {HTMLDivElement} jsonIn.target
	* @param {Object} [jsonIn.dimensions={width: Int, height: Int}]
	* @param {boolean} [jsonIn.enableUI=false]
    * @param {WebGLRenderingContext} [jsonIn.gl=undefined]
	*/
	initialize(jsonIn) {
        this.target = (jsonIn.target !== undefined) ? jsonIn.target : undefined;
        this.dimensions = (jsonIn.dimensions !== undefined) ? jsonIn.dimensions : {"width": 512, "height": 512};
        this._enableUI = (jsonIn.enableUI !== undefined) ? jsonIn.enableUI : false;
			
		if(this.target != null) {
            this.canvas = document.createElement("canvas");
            this.target.appendChild(this.canvas);
			this.setDimensions(this.dimensions.width, this.dimensions.height);

			if(jsonIn.gl !== undefined && jsonIn.gl !== null) {
                this.gl = jsonIn.gl;
            } else if(!(this.gl = Utils.getWebGLContextFromCanvas(this.canvas))) {
				alert("No WebGLRenderingContext");
				return false; 
			}
		} else alert('Target DIV required');
	};
	
	/**
	 * loadProject 
	 * @param {Project} prj
	 */
	loadProject(prj) {
        this.project = prj;
        this.project.setWebGLContext(this.gl);
		
		if(this._enableUI === true)
            this._UI = new UI(this.project).render(this.target, this.canvas);

        this._events = new SystemEvents(this, this.canvas);
        this._events.initialize();
	};
	
	/**
	 * getLoadedProject 
	 * @returns {Project}
	 */
	getLoadedProject() {
		return this.project;
	};
	
	/**
	 * getCanvas
	 * @returns {HTMLCanvasElement}
	 */
	getCanvas() {
		return this.canvas;
	};
	
	/**
	 * getEvents
	 * @returns {SystemEvents}
	 */
	getEvents() {
		return this._events;
	};
	
	/**
	 * setDimensions
	 * @param {int} width
	 * @param {int} height
	 */
	setDimensions(width, height) {
        this.dimensions = {"width": width, "height": height};

        this.canvas.setAttribute("width", this.dimensions.width);
        this.canvas.setAttribute("height", this.dimensions.height);


		if(this.project != null) {
		    if(this.project.getActiveStage() != null) {
                let actCamera = this.project.getActiveStage().getActiveCamera();
		        if(actCamera != null) {
                    let comp_proj = actCamera.getComponent(Constants.COMPONENT_TYPES.PROJECTION);
                    let comp_screenEffects = actCamera.getComponent(Constants.COMPONENT_TYPES.GPU);

                    comp_proj.setResolution(this.dimensions.width, this.dimensions.width);
                    comp_screenEffects.setArg("RGB", (function() {return new Float32Array(this.dimensions.width*this.dimensions.width*4);}).bind(this));
                }
            }
		}
	};
	
	/**
	 * @typedef {Object} SCE~getDimensions
	 * @property {int} SCE~getDimensions.width
	 * @property {int} SCE~getDimensions.height
	 */
	/**
	 * getDimensions
	 * @returns {SCE~getDimensions}
	 */
	getDimensions() {
		return this.dimensions;
	};
}
global.SCE = SCE;
module.exports.SCE = SCE;