/**
* @class
* @constructor
*/
export class UIComponentGPU_Indices {
    constructor(targetElement, selectedNode, comp, args) {
        let ah = new ActionHelpers();


        // INDICES
        let str = 	"<div id='"+targetElement.id+"_indices' style='background:rgba(0,0,0,0.5);padding-left:3px'>"+
                        "<div>indices</div>";
        let indices = comp.getBuffers()["indices"];
            if(indices !== undefined) {
                if(indices instanceof WebCLGLBuffer) {
                    let strItems = "<span title='"+indices.inData+"'>"+indices.length+"</span>";
                    str += " <span style='color:grey'> {WebCLGLBuffer "+strItems+"}</span>";
                }
            }

        str += "</div>";
        ah.appendStringChild(str, targetElement);
    }
}
global.UIComponentGPU_Indices = UIComponentGPU_Indices;
module.exports.UIComponentGPU_Indices = UIComponentGPU_Indices;